mapper.select:
    select
       LANG_ID as  langId,
       NAME_IN_ENGLISH as  nameInEnglish,
       NAME_IN_LOCAL as  nameInLocal,
       DEL_FLG as  delFlg
    from LANGUAGE_MST
    where 
LANG_ID is not null

   and     LANG_ID =#{langId}


   and     NAME_IN_ENGLISH =#{nameInEnglish}


   and     NAME_IN_LOCAL =#{nameInLocal}


   and     DEL_FLG =#{delFlg}
